mapper.sql:
	from
		member a   
	where 1=1
		AND a.memberDelNy = #{voDelNy}


			AND a.memberRegDate BETWEEN #{voDateStart} AND #{voDateEnd}
			AND memberModDate BETWEEN #{voDateStart} AND #{voDateEnd}



            AND a.memberSeq = #{voValue}
            AND a.memberName = #{voValue}	       
            AND a.memberID = #{voValue}     
           AND a.memberTypeCd = #{voValue}

mapper.select:
select memberSeq, memberID, memberPwd, memberName, memberGenderCd, memberTypeCd, memberAddr, memberDelNy, memberRegDate, memberModDate
from member
where 1=1 and memberSeq=#{memberSeq}
