mapper.sql:
	from
		orders a
	where 1=1
		and ordersDelNy = 0
		AND a.ordersDelNy = #{voDelNy}


			AND a.ordersRegDate BETWEEN #{voDateStart} AND #{voDateEnd}
			AND ordersModDate BETWEEN #{voDateStart} AND #{voDateEnd}



            AND a.ordersSeq = #{voValue}
            AND a.ordererName = #{voValue}

mapper.select:
select ordersSeq, ordererName, ordersDelNy, ordersRegDate, ordersModDate, memberSeqF
from orders
where 1=1 and ordersSeq=#{ordersSeq}

mapper.insert:
insert into orders (
	ordererName,
	ordersDelNy,
	ordersRegDate,
	ordersModDate,
	memberSeqF
) values (
	#{ordererName},
	#{ordersDelNy},
	now(),
	now(),
	#{memberSeqF}
);
