mapper.sql:
		FROM purchaseorderdetail a
		left join product b
			on a.productSeqF = b.productSeq
		left join purchaseorder c
			on a.purchaseSeqF = c.purchaseOrderSeq 
		where 1=1
		AND a.purchaseOrderDetailDelNy = #{voDelNy}


			AND a.purchaseOrderDetailRegDate BETWEEN #{voDateStart} AND #{voDateEnd}
			AND a.purchaseOrderDetailModDate BETWEEN #{voDateStart} AND #{voDateEnd}



            AND a.purchaseOrderDetailSeq = #{voValue}
            AND a.purchaseDeliverName LIKE CONCAT('%',#{voValue},'%')

mapper.update:
update purchaseorderdetail
set
	purchaseCount = #{purchaseCount}
	,purchaseDeliverName = #{purchaseDeliverName}
	,purchaseStatusCd = #{purchaseStatusCd}
	,productModDate = now()
	,productSeqF = #{productSeq}
	,purchaseSeq = #{purchaseOrderSeq}
where 1=1
	and purchaseOrderDetailSeq = #{purchaseOrderDetailSeq}

mapper.delete:
delete from purchaseorderdetail

where 1=1
	and purchaseOrderDetailSeq = #{purchaseOrderDetailSeq}
