mapper.sql:
		FROM recepit a
		where 1=1
		AND a.receiptDelNy = #{voDelNy}


			AND a.receiptRegDate BETWEEN #{voDateStart} AND #{voDateEnd}
			AND a.receiptModDate BETWEEN #{voDateStart} AND #{voDateEnd}



            AND a.receiptSeq = #{voValue}

mapper.update:
update recepit
set
	receiptCount = #{receiptCount}
	,receiptInventory = #{receiptInventory}
	,receiptStatusCd = #{receiptStatusCd}
	,receiptModDate = now()
	,purchaseOrderSeqF = #{purchaseOrderSeqF}
where 1=1
	and receiptSeq = #{receiptSeq}

mapper.delete:
delete from recepit

where 1=1
	and receiptSeq = #{receiptSeq}
